INSERT INTO `produto` (`id`, `nome_produto`, `codigo`, `descricao`, `espec_tecnica`, `foto`, `ativo`, `data_atualizacao`, `promocao`, `quantidadeCarrinho`, `marca_id`, `valorUnitario`, `departamento_id`) VALUES
(1, 'Smartphone Samsung Galaxy S20 Ultra 128GB Cosmic - Gray 12GB RAM Tela 6,9” Câm. Quádrupla + Câm. 40MP', '155586600', '<h2 class=\"description__product-title\" style=\"margin-right: 0px; margin-bottom: 30px; margin-left: 0px; padding: 0px; border: 0px; font-size: 16px; vertical-align: baseline; background: transparent; clear: both; color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\">Smartphone Samsung Galaxy S20 Ultra 128GB Cosmic - Gray 12GB RAM Tela 6,9” Câm. Quádrupla + Câm. 40MP</h2><p class=\"description__text\" style=\"margin-right: 0px; margin-bottom: 30px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\"></p><p><span style=\"color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\">Esse produto da Samsung é tudo que você pediu de um celular e com uma experiência revolucionária! Um enorme avanço na resolução de fotografias, entregando fotos profissionais a qualquer hora, pois o Galaxy S20 Ultra conta com um conjunto de quatro câmeras na traseira, permitindo cliques perfeitos e sem grande esforço, independente do ambiente. Com o recurso Space Zoom de 100X será possível chegar a lugares onde o celular nunca esteve antes. A câmera frontal de 40 megapixels oferece tecnologia de nível profissional, para que as selfies sejam nítidas, detalhadas e com o máximo de criatividade. Com 128GB de armazenamento interno, ele é perfeito para guardar todas suas fotos, músicas ou vídeos. Se ainda achar pouco, utilize um cartão MicroSD de até 1TB para expandir essa capacidade! Seu processador é Octa-Core e em conjunto dos 12GB de memória RAM no padrão LDDDR5 melhoram sua experiência de uso devido a sua combinação poderosa de hardware e software. Sua tela de 6,9\" com resolução Quad HD+, AMOLED Dinâmico 2x, certificação HDR10+ e taxa de atualização de 120Hz entregam um display grande e imersivo ao assistir seus filmes e suas séries favoritas com o máximo de qualidade. Permaneça sempre conectado nas redes sociais com a tecnologia 4G em um aparelho dual chip, além de contar com eSIM! Tenha bateria para o dia todo, já que estamos falando de 5000mAh e carregamento ultra rápido de 45W. Este celular tem proteção personalizada com biometria ultrassônica na tela, deixando seus conteúdos e dados confidenciais protegidos com máxima segurança. A cor Cosmic Gray é épica e destaca o design premium do smartphone, garantindo maior elegância em suas mãos.</span></p><p><span style=\"color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\"><br></span></p><p><iframe width=\"1110\" height=\"7800\" frameborder=\"0\" src=\"https://conteudoproduto.magazineluiza.com.br/15/155586600/index.html\" data-gtm-yt-inspected-1_25=\"true\" style=\"margin: 0px; padding: 0px; vertical-align: baseline; background: transparent; color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif; text-align: -webkit-center;\"></iframe><span style=\"color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\"><br></span><br></p>', '<table class=\"description__box--wildSand\" style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: rgb(244, 244, 244); border-spacing: 0px; width: 1319px; float: left; color: rgb(64, 64, 64); font-family: arial, tahoma, verdana, sans-serif;\"><tbody style=\"margin: 0px; padding: 0px; border-style: initial; border-color: initial; vertical-align: baseline; background: transparent;\"><tr style=\"margin: 0px; padding: 0px; border-style: initial; border-color: initial; vertical-align: baseline; background: transparent;\"><td class=\"description__information-left\" style=\"margin: 0px; padding: 18px; border-width: 0px; border-style: initial; border-color: initial; vertical-align: baseline; background: transparent; width: 253.047px; float: left; font-weight: 700;\">Informações técnicas</td><td class=\"description__information-righ